mapper.insert:
INSERT INTO department (
    dept_cd
  , parent_dept_cd
  , dept_name
  , manager_emp_id
  , create_at
  , company_no
) VALUES (
    #{deptCd}
  , #{parentDeptCd}
  , #{deptName}
  , #{managerEmpId}
  , SYSDATE
  , #{companyNo}
)

mapper.delete:
DELETE FROM department
WHERE company_no = #{companyNo}
  AND dept_cd = #{deptCd}

mapper.update:
UPDATE department

   dept_name = #{deptName}, 
   manager_emp_id = #{managerEmpId}, 

WHERE dept_cd = #{deptCd}
  AND company_no = #{companyNo}

UPDATE department
SET
    parent_dept_cd = #{parentDeptCd}
  , dept_name = #{deptName}
  , manager_emp_id = #{managerEmpId}
  , create_at = #{createAt}
WHERE dept_cd = #{deptCd}
  AND company_no = #{companyNo}

mapper.select:
SELECT manager_emp_id
FROM department
WHERE dept_cd = #{deptCd}
  AND company_no = #{companyNo}
